
exports.User = function(k, opts){

    var opts = opts || {},
        isSalt = !!opts.isSalt;

    var User = k.create({
        email: "string",
        password: "string",
        nickname: "string",
        properties: "object",
        groups: "array",
        wikis: "array"
    }, "User");

    User.signin = function(condition, callback){
        this.queryOne(condition, k.callback(callback));
    };

    User.signup = function(instance, callback){
        this.insertOne(instance, k.callback(callback));
    };
    
    return User;
};

